/*global tinymce:true */

tinymce.PluginManager.add('myversion', function(editor, url) {

  function showDialog(imageList) {
    var win;

    // Simple default dialog
    win = editor.windowManager.open({
      title: 'Show content versions',
      width: '900px',
      height: '600px',
      inline: true,
      url: url + '/modal.html',
      buttons: [{
        text: 'Copy to editor',
        onclick: function() {
          editor.execCommand('mceVersionsCopyContent');
        }
      },{
        text: 'Close',
        onclick: 'close'
      }]
    });
  }

  editor.addButton('myversion', {
    text: 'Versions',
    icon: false,
    tooltip: 'Show all content version',
    onclick: showDialog
  });

});